	public void introducirTarjeta(Tarjeta tarjeta) {
		if (!listaDeTarjetas.isEmpty()) {
			Tarjeta tarjetaAux = buscarTarjeta(tarjeta);
			if (tarjetaAux==null) {
				System.out.println("No existe");
			listaDeTarjetas.add(tarjeta);
			System.out.println(tarjeta.toString());
			
			}else {
			System.out.println("Ya existe la tarjeta");
			}
		

		}else {
			listaDeTarjetas.add(tarjeta);
			System.out.println(tarjeta.toString());
		}
		
		
	}

