		//Variables del metodo
		boolean existe;
		String denominacion,cantante;
		double precio,duracion;

		//comprobacion mediante un atributo (clave primaria) si esta ya existe

		System.out.println("Introduce la denominacion"); 
		denominacion=in.next();//tecleamos nuestro campo clave denominacion
		existe=buscarArticulo(denominacion);//buscar articulo mediante denominacion y guardamos  en la variable se guarda (existe==true/existe==false)

		if (!existe) {//si  !existe significa que no existe un articulo con esa denominacion
			// añadimos los atributos necesarios del objeto
			do{
				System.out.println("Introduce el precio del Disco:");
				precio=in.nextInt();
			}while(precio<=0);//validamos
			System.out.println("introduce el cantante");
			cantante=in.next();
			do{
				System.out.println("Introduce la duracion:");
				duracion=in.nextDouble();
			}while(duracion<=0);//validamos
			//Una vez tenemos todos los campos necesarios hacemos un new			
			Disco disco = new Disco(denominacion, precio, cantante, duracion);
			insertarArticulo(disco);//una vez tenemos nuestro disco con sus campos creado lo insertamos en un array list
		} else {//significa que ya existe un articulo con esa denominacion
			System.out.println("Esa denominacion ya esta siendo usada");
		}

	}
	//buscar
	public boolean buscarArticulo(String denominacion) {

		boolean existe = false;

		for(Articulo articulo : listaArticulos)
			if(articulo.getDenominacion().equalsIgnoreCase(denominacion))
				existe=true;
		return existe;
	}
	//insertar
	public void insertarArticulo(Articulo articulo) {
		if (listaArticulos.add(articulo)) {
			System.out.println("Se añadio correctamente "+articulo.toString());
		}

	}
